client_script:
frappe.ui.form.on('Hero Section', {
	refresh: function(frm) {
		// Add character counter for slide_description
		if (frm.doc.slide_description !== undefined) {
			frm.set_df_property('slide_description', 'description', 'Paragraph text displayed below subtitle (Max 200 characters) - Current: ' + (frm.doc.slide_description ? frm.doc.slide_description.length : 0) + '/200');
		}
	},
	slide_description: function(frm) {
		// Update character counter when description changes
		if (frm.doc.slide_description !== undefined) {
			var currentLength = frm.doc.slide_description ? frm.doc.slide_description.length : 0;
			frm.set_df_property('slide_description', 'description', 'Paragraph text displayed below subtitle (Max 200 characters) - Current: ' + currentLength + '/200');

			// Change field color if approaching limit
			if (currentLength > 180) {
				frm.set_df_property('slide_description', 'bg_color', '#ffebee');
			} else if (currentLength > 150) {
				frm.set_df_property('slide_description', 'bg_color', '#fff3e0');
			} else {
				frm.set_df_property('slide_description', 'bg_color', '');
			}
		}
	}
});
